\begin{soln}
  Follow this calculations below:
  \begin{align*}
    &\int_{1}^{x} \floor{t}(\floor{t}+1)f(t)\mathrm{d}t \\
    &= \int_{1}^{\floor{x}} \floor{t}
    (\floor{t}+1)f(t)\mathrm{d}t
    + \int_{\floor{x}}^{x} \floor{t}
    (\floor{t}+1)f(t)\mathrm{d}t \\
    &= \sum_{i=1}^{\floor{x}-1} \int_{i}^{i+1}
    \floor{t}(\floor{t}+1)f(t)\mathrm{d}t
    + \floor{x}(\floor{x}+1)
    \int_{\floor{x}}^{x} f(t)\mathrm{d}t \\
    &= \sum_{i=1}^{\floor{x}-1} i(i+1) \int_{i}^{i+1}
    f(t)\mathrm{d}t + \floor{x}(\floor{x}+1)
    \int_{\floor{x}}^{x} f(t)\mathrm{d}t \\
    &= 2 \sum_{i=1}^{\floor{x}-1} \sum_{j=1}^{i}
    j \int_{i}^{i+1} f(t)\mathrm{d}t
    + \floor{x}(\floor{x}+1)\int_{\floor{x}}^{x}
    f(t)\mathrm{d}t \\
    &= 2\sum_{j=1}^{\floor{x}-1} \sum_{i=j}^{\floor{x}-1}
    j \int_{i}^{i+1} f(t)\mathrm{d}t+\floor{x}
    (\floor{x}+1) \int_{\floor{x}}^{x} f(t)\mathrm{d}t \\
    &= 2\sum_{j=1}^{\floor{x}-1}
    j \int_{j}^{\floor{x}} f(t)\mathrm{d}t
    + \floor{x}(\floor{x}+1) \int_{\floor{x}}^{x}
    f(t)\mathrm{d}t \\
    &= 2 \left(\sum_{j=1}^{\floor{x}-1}
    j \int_{j}^{\floor{x}} f(t)\mathrm{d}t
    + j \int_{\floor{x}}^{x} f(t)\mathrm{d}t\right)
    + (\floor{x}(\floor{x}+1)-(\floor{x}-1)\floor{x})
    \int_{\floor{x}}^{x} f(t)\mathrm{d}t \\
    &= 2 \sum_{j=1}^{\floor{x}-1} j \int_{j}^{x}
    f(t)\mathrm{d}t + \left(\int_{\floor{x}}^{x} f(t)
    \mathrm{d}t\right) \cdot 2 \floor{x} \\
    &= 2 \sum_{j=1}^{\floor{x}} j \int_{j}^{x}
    f(t)\mathrm{d}t
  .\qedhere\end{align*}

\end{soln}
